import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { propertyService } from "@/_services";
import DropdownMenu from "../../components/public/DropdownMenu.js";
import Error from "@/_utils/Error";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronRight } from "@fortawesome/free-solid-svg-icons";
import { faChevronLeft } from "@fortawesome/free-solid-svg-icons";
import { faStar } from "@fortawesome/free-solid-svg-icons";

const Logement = () => {
  const { id } = useParams();
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    propertyService
      .getProperty(id)
      .then((response) => {
        setProperty(response.data);
      })
      .catch((error) => console.error("Error fetching data:", error))
      .finally(() => setLoading(false));

    //eslint-
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!property) {
    return <Error />;
  }

  const hostName = property && property.host && property.host.name;
  const hostPicture = property && property.host && property.host.picture;

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === property.pictures.length - 1 ? 0 : prevIndex + 1
    );
  };

  const previousImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? property.pictures.length - 1 : prevIndex - 1
    );
  };

  const rating = parseInt(property.rating);

  return (
    <div>
      <div className="slide">
        <img src={property.pictures[currentImageIndex]} alt={property.title} />
        <div className="arrow-icons">
          <FontAwesomeIcon
            className="fa-chevron-left"
            icon={faChevronLeft}
            size="2xl"
            onClick={previousImage}
            onDoubleClick={(e) => {
              e.preventDefault();
              e.stopPropagation(); // Prevents the event from bubbling up to parent elements
            }}
          />
          <FontAwesomeIcon
            className="fa-chevron-right"
            icon={faChevronRight}
            onClick={nextImage}
            onDoubleClick={(e) => {
              e.preventDefault();
              e.stopPropagation(); // Prevents the event from bubbling up to parent elements
            }}
          />
        </div>
      </div>
      <div className="logement-container1">
        <div className="logement-info">
          {property && <h1>{property.title}</h1>}
          {property && <h2>{property.location}</h2>}
        </div>
        <div className="host-info">
          {hostName && ( // Check if hostName exists before rendering
            <p className="host-name">
              {hostName.split(" ")[0]} <br /> {hostName.split(" ")[1]}
            </p>
          )}
          {hostPicture && ( // Check if hostPicture exists before rendering
            <img src={hostPicture} alt="Host" className="host-image" />
          )}
        </div>
      </div>
      <div className="logement-container2">
        {property && property.tags && (
          <div className="tags">
            {property.tags.map((tag, index) => (
              <h3 key={index} className="logement-tag">
                {tag}
              </h3>
            ))}
          </div>
        )}
        <div className="rating">
          <FontAwesomeIcon
            icon={faStar}
            className={rating >= 1 ? "fa-star colored" : "fa-star"}
          />
          <FontAwesomeIcon
            icon={faStar}
            className={rating >= 2 ? "fa-star colored" : "fa-star"}
          />
          <FontAwesomeIcon
            icon={faStar}
            className={rating >= 3 ? "fa-star colored" : "fa-star"}
          />
          <FontAwesomeIcon
            icon={faStar}
            className={rating >= 4 ? "fa-star colored" : "fa-star"}
          />
          <FontAwesomeIcon
            icon={faStar}
            className={rating >= 5 ? "fa-star colored" : "fa-star"}
          />
        </div>
      </div>
      <div className="dropdownmenu">
        {property?.description && (
          <DropdownMenu
            title="Description"
            content={property.description}
            customClassName="description-menu"
          />
        )}
        {property?.equipments && (
          <DropdownMenu
            title="Ã‰quipements"
            content={
              <div>
                {property.equipments.map((equipment, index) => (
                  <p key={index} style={{ padding: "0em", marginTop: "0em" }}>
                    {equipment}
                  </p>
                ))}
              </div>
            }
            customClassName="equipments-menu"
          />
        )}
      </div>
    </div>
  );
};

export default Logement;
