import React, { useState } from "react";
import cover from "../../images/Image_source_2.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronUp } from "@fortawesome/free-solid-svg-icons";

const About = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };
  return (
    <div>
      <div className="about_cover">
        <img src={cover} alt="about_cover_image" />
      </div>
      <div className="dropdown-menu-container">
        <div className="dropdown-menu" onClick={toggleDropdown}>
          <p>Fiabilité</p>
          <FontAwesomeIcon
            className="fa-chevron-up"
            icon={faChevronUp}
            size="2x"
          />
          {isDropdownOpen && (
            <div className="dropdown-content">
              {/* Dropdown content here */}
              <p>Dropdown content for Fiabilité</p>
            </div>
          )}
        </div>
        <div className="dropdown-menu">
          <p>Respect</p>
          <FontAwesomeIcon
            className="fa-chevron-up"
            icon={faChevronUp}
            size="2x"
          />
        </div>
        <div className="dropdown-menu">
          <p>Service</p>
          <FontAwesomeIcon
            className="fa-chevron-up"
            icon={faChevronUp}
            size="2x"
          />
        </div>
        <div className="dropdown-menu">
          <p>Sécurité</p>
          <FontAwesomeIcon
            className="fa-chevron-up"
            icon={faChevronUp}
            size="2x"
          />
        </div>
      </div>
    </div>
  );
};

export default About;
