import React from "react";
import { Link } from "react-router-dom";
import "./card.css";

const card = ({ property, image }) => {
  const history = useHistory();
  const handleClick = () => {
    // Function to handle click event
    history.push(`http://localhost:3000/logements/${property.id}`);
  };
  return (
    <article className="card_article" onClick={handleClick}>
      <img src={image} alt={property.title} className="card_image" />
      <div className="card_title">{property.title}</div>
    </article>
  );
};

export default card;
